import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

const BASE_URL = `https://jsonplaceholder.typicode.com/`;

axios.defaults.baseURL = BASE_URL;

export const fetchTodos = createAsyncThunk(
  'todos/fetchAll',
  async (_, thunkAPI) => {
    try {
      const res = await axios.get('/todos/?_limit=8');
      return res.data;
    } catch (err) {
      return thunkAPI.rejectWithValue(err.message);
    }
  }
);

export const addTodos = createAsyncThunk(
  'todos/addTodos',
  async (todo, thunkAPI) => {
    try {
      const res = await axios.post('/todos', todo);
      return res.data;
    } catch (err) {
      return thunkAPI.rejectWithValue(err.message);
    }
  }
);

export const handleDeleteFulfilled = (state, { payload }) => {
  state.isLoading = false;
  state.error = null;
  // Use filter to create a new array without the deleted todo
  state.items = state.items.filter(todo => todo.id !== payload.id);
};

export const updateTodos = createAsyncThunk(
  'todos/updateTodos',
  async ({ id, title }, thunkAPI) => {
    try {
      const res = await axios.put(`/todos/${id}`, { title });
      return res.data;
    } catch (err) {
      return thunkAPI.rejectWithValue(err.message);
    }
  }
);
