import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, BoxTitle, ButtonDelete, ButtonEdit } from "./AddTodo.styled";
import { getError, getFilter, getIsLoading, getTodos } from "../../redux/selectors";
import { fetchTodos, deleteTodos, updateTodos } from "../../redux/operations";
import InputTodo from "../../components/InputTodo/InputTodo";
import Loader from "../../components/Loader/Loader";

const AddTodo = () => {
  const dispatch = useDispatch();
  const todos = useSelector(getTodos);
  const isLoading = useSelector(getIsLoading);
  const error = useSelector(getError);
  const filter = useSelector(getFilter);

  useEffect(() => {
    dispatch(fetchTodos());
  }, [dispatch]);

  const [editingId, setEditingId] = useState(null);
  const [updatedTitle, setUpdatedTitle] = useState('');

  const findTodos = () => {
    const normalizedFilter = filter ? filter.toLowerCase() : '';

    if (!todos) {
      return [];
    }

    return todos.filter(todo =>
      todo.title.toLowerCase().includes(normalizedFilter)
    );
  };

  const filteredContacts = findTodos();

  const handleEdit = (id) => {
    setEditingId(id);
    const todoToEdit = todos.find(todo => todo.id === id);
    setUpdatedTitle(todoToEdit.title);
  };

  const handleSave = () => {
    dispatch(updateTodos({ id: editingId, title: updatedTitle }));
    setEditingId(null);
  };

  return (
    <>
      {error && <p>Error: {error}</p>}
      {isLoading && <Loader />}
      <InputTodo />
      <Box>
        {filteredContacts.map(({ id, title }) => (
          <BoxTitle key={id}>
            {editingId === id ? (
              <>
                <input
                  type="text"
                  value={updatedTitle}
                  onChange={(e) => setUpdatedTitle(e.target.value)}
                />
                <ButtonEdit onClick={handleSave}>Save</ButtonEdit>
              </>
            ) : (
              <>
                {title}
                <ButtonDelete onClick={() => dispatch(deleteTodos(id))} />
                <ButtonEdit onClick={() => handleEdit(id)}>Edit</ButtonEdit>
              </>
            )}
          </BoxTitle>
        ))}
      </Box>
    </>
  );
};

export default AddTodo;
