import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, BoxTitle, ButtonDelete, ButtonEdit } from "./AddTodo.styled";
import { getError, getFilter, getIsLoading, getTodos } from "../../redux/selectors";
import { fetchTodos, deleteTodos, updateTodos } from "../../redux/operations";
import InputTodo from "../../components/InputTodo/InputTodo";
import Loader from "../../components/Loader/Loader";

const AddTodo = () => {
  const dispatch = useDispatch();
  const todos = useSelector(getTodos);
  const isLoading = useSelector(getIsLoading);
  const error = useSelector(getError);
  const filter = useSelector(getFilter);

  useEffect(() => {
    dispatch(fetchTodos());
  }, [dispatch]);

  const [editingId, setEditingId] = useState(null);

  // Use local state for edited title
  const [editedTitle, setEditedTitle] = useState('');

  const findTodos = () => {
    const normalizedFilter = filter ? filter.toLowerCase() : '';

    if (!todos) {
      return [];
    }

    return todos.filter(todo =>
      todo.title.toLowerCase().includes(normalizedFilter)
    );
  };

  const filteredTodos = findTodos();

  const handleEdit = (id, title) => {
    setEditingId(id);
    // Set the initial value for the edited title
    setEditedTitle(title);
  };

  const handleSave = () => {
    // Dispatch the updateTodos action with the edited title
    dispatch(updateTodos({ id: editingId, title: editedTitle }));
    // Reset editing state
    setEditingId(null);
  };

  return (
    <>
      {error && <p>Error: {error}</p>}
      {isLoading && <Loader />}
      <InputTodo />
      <Box>
        {filteredTodos.map(({ id, title }) => (
          <BoxTitle key={id}>
            {editingId === id ? (
              <>
                <input
                  type="text"
                  value={editedTitle}
                  onChange={(e) => setEditedTitle(e.target.value)}
                />
                <ButtonEdit onClick={handleSave}>Save</ButtonEdit>
              </>
            ) : (
              <>
                {title}
                <ButtonDelete onClick={() => dispatch(deleteTodos(id))} />
                <ButtonEdit onClick={() => handleEdit(id, title)}>Edit</ButtonEdit>
              </>
            )}
          </BoxTitle>
        ))}
      </Box>
    </>
  );
};

export default AddTodo;
