import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, BoxTitle, ButtonDelete } from "./AddTodo.styled";
import { getError, getFilter, getIsLoading, getTodos } from "../../redux/selectors";
import { fetchTodos, deleteTodos } from "../../redux/operations";
import InputTodo from "../../components/InputTodo/InputTodo";
import { Loader } from "../../components/Loader/Loader";

const AddTodo = () => {
  const dispatch = useDispatch();
  const todos = useSelector(getTodos);
  const isLoading = useSelector(getIsLoading);
  const error = useSelector(getError);
  const filter = useSelector(getFilter);

  useEffect(() => {
    dispatch(fetchTodos());
  }, [dispatch]);

  const [showLoader, setShowLoader] = useState(true);

  const findTodos = () => {
    const normalizedFilter = filter ? filter.toLowerCase() : '';

    if (!todos) {
      return [];
    }

    return todos.filter(todo =>
      todo.title.toLowerCase().includes(normalizedFilter)
    );
  };
  const filteredContacts = findTodos();

  const handleDelete = (id) => {
    setShowLoader(true);
    // Simulate an asynchronous action with a delay
    setTimeout(() => {
      dispatch(deleteTodos(id));
      setShowLoader(false);
    }, 1000); // Adjust the delay as needed
  };

  return (
    <>
      {error && <p>Error: {error}</p>}
      {showLoader && <Loader />}
      {!showLoader && (
        <>
          <InputTodo />
          <Box>
            {filteredContacts.map(({ id, title }) => (
              <BoxTitle key={id}>
                {title}
                <ButtonDelete onClick={() => handleDelete(id)} />
              </BoxTitle>
            ))}
          </Box>
        </>
      )}
    </>
  );
};

export default AddTodo;
