// AddTodo.js

import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, BoxTitle, ButtonDelete, ButtonEdit } from "./AddTodo.styled";
import { getError, getFilter, getIsLoading, getTodos } from "../../redux/selectors";
import { fetchTodos, deleteTodos, updateTodos } from "../../redux/operations";
import InputTodo from "../../components/InputTodo/InputTodo";
import Loader from "../../components/Loader/Loader";

const AddTodo = ({todo}) => {
  const dispatch = useDispatch();
  const todos = useSelector(getTodos);
  const isLoading = useSelector(getIsLoading);
  const error = useSelector(getError);
  const filter = useSelector(getFilter);

  useEffect(() => {
    dispatch(fetchTodos());
  }, [dispatch]);

  const [editingId, setEditingId] = useState(null);
  const [editedTitle, setEditedTitle] = useState(""); // Added state for edited title

  const findTodos = () => {
    const normalizedFilter = filter ? filter.toLowerCase() : '';

    if (!todos) {
      return [];
    }

    return todos.filter((todo) =>
      todo.title.toLowerCase().includes(normalizedFilter)
    );
  };

  const filteredContacts = findTodos();

  const handleEdit = (id, title) => {
    setEditingId(id);
    setEditedTitle(title); // Set the initial value for the edited title
  };
  const handleUpdate = (newTitle, id) => {
    dispatch(updateTodos({ id: todo.id, title: newTitle }));
  };

  const handleSave = (id) => {
    dispatch(updateTodos({ id, title: editedTitle })); // Use the editedTitle state
    setEditingId(null);
  };

  return (
    <>
      {error && <p>Error: {error}</p>}
      {isLoading && <Loader />}
      <InputTodo />
      <Box>
        {filteredContacts.map(({ id, title }) => (
          <BoxTitle key={id}>
            {editingId === id ? (
              <>
                <input
                  type="text"
                  value={editedTitle}
                  onChange={(e) => handleUpdate(e.target.value)}
                />
                <ButtonEdit onClick={() => handleSave(id)}>Save</ButtonEdit>
              </>
            ) : (
              <>
                {title}
                <ButtonDelete onClick={() => dispatch(deleteTodos(id))} />
                <ButtonEdit onClick={() => handleEdit(id, title)}>Edit</ButtonEdit>
              </>
            )}
          </BoxTitle>
        ))}
      </Box>
    </>
  );
};

export default AddTodo;
